css:
.button__talk-all-author-articles {
    display: block;
    margin-right: auto;
    margin-left: auto;
    color: #04b;
    border: 1px solid #04b;
    background-color: #fff;
}

.snippet__content {
    margin-left: 2px;
}

.snippet__thumb {
    order: 2;
}

.snippet__talk-user-profile {
    margin-right: auto;
    margin-left: auto;
}

.snippet.unit_rect {
    display: flex;
    flex-direction: row;
    margin-top: 18px;
}

.image-simple_rounded {
    border-radius: 50%;
}

.button.unit_rect {
    margin-top: 18px;
}

.page__content {
    padding-bottom: 30px;
}

.social__talk_container {
    margin-top: 24px;
    margin-bottom: 20px;
}

.social__talk_container .label {
    display: none;
}

.share__item {
    cursor: pointer;
}

.share__item.button_pressed {
    transform: none;
}

.share__item-wrapper:hover .share__item_type_facebook {
    background: #33518a !important;
}

.share__item-wrapper:hover .share__item_type_vkontakte {
    background: #446c91 !important;
}

.share__item-wrapper:hover .share__item_type_odnoklassniki {
    background: #e36e00 !important;
}

.share__item-wrapper:hover .share__copy {
    background-color: #d9d9d9;
}

.cmnt-main {
    margin-top: 12px;
}

.header {
    height: 70px;
    box-sizing: border-box;
    padding: 10px 30px 10px 0;
    background-image: url(data:image/svg+xml;base64,);
    background-repeat: no-repeat;
    background-position-y: center;
}

.header .header-title,
.header__title-link .header-title {
    visibility: hidden;
}

.header .header__title-link {
    height: 100%;
}

.header .header__aside {
    top: 11px;
}

.image_type_block.image_caption_on .image__picture {
    background-position-x: center;
}

.image_type_block.image_caption_on .image__capture {
    text-align: center;
}

.image_type_block.image_type_block.image_preloaded.image__talk_narrow .image__picture {
    max-height: none !important;
    background-image: none !important;
}

.image_type_block.image_type_block.image_preloaded.image__talk_narrow .image__img {
    position: relative;
    opacity: 1;
}

.image_type_block.image_type_block.image_preloaded.image__talk_narrow .image__ratio {
    padding: 0 !important;
}

.social__talk_container {
    clear: both;
}

.social__talk_container .talk-like {
    margin-bottom: 20px;
}

.talk-footer {
    margin-top: 20px;
    display: flex;
    flex-flow: column;

    text-align: center;
    font-size: 14px;
}

.talk-footer .divider {
    max-width: 150px;
    margin: 5px auto 2px;
    width: 100%;
}

.animation__container,
.animation__caption {
    text-align: center;
}

@media screen and (min-width: 801px) {
    .page_platform_touch-phone .page__container,
    .page_platform_touch-phone .modal,
    .page_platform_touch-phone .alert,
    .page_platform_touch-phone .sticky_fixed .sticky__content {
        width: 700px;
    }

    .page_platform_touch-phone .sticky_fixed .sticky__content,
    .page_platform_touch-phone .modal,
    .page_platform_touch-phone .alert {
        margin-left: -350px;
    }

    .social__talk_container .talk-like {
        margin-bottom: 0;
    }

    .social__talk_container {
        display: flex;
        align-items: center;
        justify-content: space-between;
    }

    .social__talk_col {
        display: flex;
        align-items: center;
    }

    .social__talk_col .share {
        width: 392px;
    }

    .social__talk_col .label {
        display: block;
        margin-right: 12px;
        color: #999;
    }

    .image__talk_narrow.image_type_block.image_caption_on {
        float: left;
        width: auto;
        margin: 32px 32px 24px 0;
        clear: both;
    }

    .image__talk_narrow.image_type_block.image_caption_on .image__img {
        position: static;
    }
}

.page_platform_desktop .layout_branding_off .header__container {
    padding: 0;
    margin-top: 0;
    height: 100%;
    width: 100%;
    background: none;
}

.page_platform_desktop .layout__main {
    margin: auto;
}

.page_platform_desktop .layout__left,
.page_platform_desktop .layout__right {
    display: none;
}
