let log_source = 'ya-money-dump';
let series = {
    cluster='soupostat',
    service='soupostat',
    sensor='*_ya-money-dump',
    kind='table'
};

let df = derivative(series);
let last_val = last(series);
let max_val = max(series);
let min_val = min(series);

// check is monotonously increasing function df > 0
let min_df = min(df);
let last_df = last(df);

// make alerts
let type = 'no data';
no_data_if(max_val <= 1);

let type = 'decrease';
alarm_if(min_df < 0);

let type = 'last day equal';
alarm_if(
    (max_val != min_val)
    && (last_df == 0)
);

let type = 'equal';
warn_if(min_df == 0);

annotations.description:
{{^is_ok}}
Bigrt states '{{expression.System}}' are failing for {{expression.Cluster}}.
Fail rate is {{expression.Value}}.
{{/is_ok}}

expression.program:
let TotalRaw = {
    project="crypta_graph",
    cluster="michurin-test",
    service="michurin-test",
    host="cluster",
    sensor="epochs_total",
    system="michurin"
    };
let FailedRaw = {
    project="crypta_graph",
    cluster="michurin-test",
    service="michurin-test",
    host="cluster",
    sensor="epochs_failed",
    system="michurin"
    };

let TotalAvg = avg(group_lines('avg', TotalRaw));
let FailRate = FailedRaw / (TotalAvg + 1e-2);

let SumFailRate = group_lines('sum', FailRate);

let SumP80 = percentile(80, SumFailRate);

let Value = to_fixed(SumP80, 3);
let Cluster = "michurin-test";
let System = "michurin";

alarm_if(SumP80 > 0.1);
warn_if(SumP80 > 0.05);

