select b.bid, b.cid, distinct.b, straight_join.a, t.bannername, v.phone from banners b join vcards v on v.vcard_id = b.vcard_id and v.id = b.id inner join (select distinct a.format, distinct_bannername, id, straight_join_t.yql from texts t) t on t.id = b.text_id inner join phrases s using(id) where b.reverse_domain is null and v.phone is not null and v.phone != ? and input = ? and v.id = ? group by v.phone order by null