public open class GroupActionsEvents {
    open fun selectMessage(order: Int, mid: Long): EventusEvent {
        if (order < 0) {
            return EventusEvent.eventCreationErrorEvent(EventNames.GROUP_MESSAGE_SELECT, "Order must be equal or greater then 0. Was: ${order}")
        }
        return EventusEvent.newClientEvent(EventNames.GROUP_MESSAGE_DESELECT, ValueMapBuilder.userEvent().addOrder(order).addMid(mid))
    }

    open fun deselectMessage(order: Int, mid: Long): EventusEvent {
        if (order < 0) {
            return EventusEvent.eventCreationErrorEvent(EventNames.GROUP_MESSAGE_DESELECT, "Order must be equal or greater then 0. Was: ${order}")
        }
        return EventusEvent.newClientEvent(EventNames.GROUP_MESSAGE_DESELECT, ValueMapBuilder.userEvent().addOrder(order).addMid(mid))
    }

    open fun deleteSelectedMessages(): EventusEvent {
        return EventusEvent.newClientEvent(EventNames.GROUP_DELETE_SELECTED, ValueMapBuilder.userEvent())
    }

    open fun markAsReadSelectedMessages(): EventusEvent {
        return EventusEvent.newClientEvent(EventNames.GROUP_MARK_AS_READ_SELECTED, ValueMapBuilder.userEvent())
    }

    open fun markAsUnreadSelectedMessages(): EventusEvent {
        return EventusEvent.newClientEvent(EventNames.GROUP_MARK_AS_UNREAD_SELECTED, ValueMapBuilder.userEvent())
    }

}

