package com.yandex.xplat.eventus

import com.yandex.xplat.eventus.common.EventusEvent
import com.yandex.xplat.eventus.common.ValueMapBuilder

public open class MultiAccountEvents {
    open fun addNewAccount(): EventusEvent {
        return EventusEvent.newClientEvent(EventNames.MULTIACCOUNT_ADD_NEW_ACCOUNT, ValueMapBuilder.userEvent())
    }

    open fun logoutFromAccount(): EventusEvent {
        return EventusEvent.newClientEvent(EventNames.MULTIACCOUNT_LOGOUT_FROM_ACCOUNT, ValueMapBuilder.userEvent())
    }

    open fun switchToAccount(uid: Long?): EventusEvent {
        if (uid == null) {
            return EventusEvent.eventCreationErrorEvent(EventNames.MULTIACCOUNT_SWITCH_TO_ACCOUNT, "UID must not be null. Was: ${uid}")
        }
        return EventusEvent.newClientEvent(EventNames.MULTIACCOUNT_SWITCH_TO_ACCOUNT, ValueMapBuilder.userEvent().addUid(uid))
    }

}

