public open class ZeroSuggestComponent: MBTComponent {
    open override fun assertMatches(model: App, application: App): Unit {
        val zeroSuggestModel = ZeroSuggestFeature.`get`.castIfSupported(model)
        val zeroSuggestApplication = ZeroSuggestFeature.`get`.castIfSupported(application)
        if (zeroSuggestModel == null || zeroSuggestApplication == null) {
            return
        }
        val actualZeroSuggest = zeroSuggestApplication.getZeroSuggest()
        val expectedZeroSuggest = zeroSuggestModel.getZeroSuggest()
        assertInt32Equals(expectedZeroSuggest.size, actualZeroSuggest.size, "The number of suggestions in the list is incorrect")
        for (i in (0 until actualZeroSuggest.size step 1)) {
            assertStringEquals(expectedZeroSuggest[i], actualZeroSuggest[i], "Zero suggest is incorrect")
        }
    }

    open override fun tostring(): String {
        return "ZeroSuggestComponent"
    }

    open override fun getComponentType(): String {
        return ZeroSuggestComponent.type
    }

    companion object {
        @JvmStatic val type: String = "ZeroSuggestComponent"
    }
}

