import Foundation

open class FolderNavigatorModel: FolderNavigator {
  public var model: MessageListDisplayModel
  public init(_ model: MessageListDisplayModel) {
    self.model = model
  }

  @discardableResult
  open func getFoldersList() -> YSArray<Folder> {
    let folders: YSArray<Folder> = YSArray()
    model.accountDataHandler.getCurrentAccount().folderToMessages.__forEach {
      _, folder in
      folders.push(Folder(folder))
    }
    return folders
  }

  open func goToFolder(_ folderDisplayName: String) {
    model.setCurrentFolder(Folder(folderDisplayName))
  }

  @discardableResult
  open func getCurrentFolder() -> Folder {
    return model.getCurrentFolder()
  }
}
