import Foundation

open class MarkableImportantModel: MarkableImportant {
  private var model: MessageListDisplayModel
  public init(_ model: MessageListDisplayModel) {
    self.model = model
  }

  open func markAsImportant(_ order: Int32) {
    for mid in model.getMessageThreadByOrder(order) {
      model.storedMessage(mid).mutableHead.important = true
    }
  }

  open func markAsUnimportant(_ order: Int32) {
    for mid in model.getMessageThreadByOrder(order) {
      model.storedMessage(mid).mutableHead.important = false
    }
  }
}
