import Foundation

open class MarkableReadModel: MarkableRead {
  private var model: MessageListDisplayModel
  public init(_ model: MessageListDisplayModel) {
    self.model = model
  }

  open func markAsRead(_ order: Int32) {
    for mid in model.getMessageThreadByOrder(order) {
      log("Marking mid \(mid) as read")
      model.storedMessage(mid).mutableHead.read = true
    }
  }

  open func markAsUnread(_ order: Int32) {
    for mid in model.getMessageThreadByOrder(order) {
      log("Marking mid \(mid) as unread")
      model.storedMessage(mid).mutableHead.read = false
    }
  }
}
