'use strict';

const checkYaclid = require('./../../../utils/check-yaclid');

describe('Check yaclid', () => {
    test('should cut yaclid if it\'s length more than 300', () => {
        const invalidYaclid =
            '';

        const actual = checkYaclid(invalidYaclid);

        const expected = {
            error: true,
            yaclid:
                ''
        };

        expect(actual).toEqual(expected);
    });
});
